function calculateTotals(cart) {
  const result = {
    overall: 0,
    categories: {},
    brands: {}
  };

  cart.forEach(item => {
    result.overall += item.totalPrice;

    if (!result.categories[item.category]) {
      result.categories[item.category] = 0;
    }
    result.categories[item.category] += item.totalPrice;

    if (!result.brands[item.brand]) {
      result.brands[item.brand] = 0;
    }
    result.brands[item.brand] += item.totalPrice;
  });

  return result;
}

const totals = calculateTotals({{.Trigger.cart}}.cart_list);

const result = [
  {
    type: "overall",
    name: "Payment Gateway",
    value: totals.overall
  },
];

// Adding category totals
for (const category in totals.categories) {
  result.push({
    type: "category",
    name: category,
    value: totals.categories[category]
  });
}

// Adding brand totals
for (const brand in totals.brands) {
  result.push({
    type: "brand",
    name: brand,
    value: totals.brands[brand]
  });
}

result

const data = {{.Zepto_Coupons_Each.output}};
const coupons = [];

data.forEach(group => {
  if (Array.isArray(group.output)) {
    group.output.forEach(coupon => {
      coupons.push(coupon);
    });
  }
});

const uniqueCoupons = [];
const seenCodes = {};

coupons.forEach(coupon => {
  if (!seenCodes[coupon.code]) {
    seenCodes[coupon.code] = true;
    uniqueCoupons.push(coupon);
  }
});

